package firstpack;

public class Main {

    public static void main(String[] args) {
        // TODO Auto-generated method stub

        System.out.println("For loop test");

        for(int bal = 25; bal >= -3; bal--) {
            System.out.println("the value of J is currently: " + bal);
            System.out.println("Going down one level");
        }

        System.out.println("Looks like we can go no lower!");
        System.out.println("Dum dum dUMMMMMMM");
        System.out.println("The final push.");


    }

}
